import { SAVE_BOOK_REQUEST, SAVE_BOOK_SUCCESS, SAVE_BOOK_FAILURE } from './bookTypes';

import axios from 'axios';

export const saveBook = book =>{

    return dispatch =>{
        dispatch(savebook());
        axios.get("http://localhost:8081/rest/books", book).
        then(response=>{
                dispatch(savebookSuccess(response.data));
        }).
        catch(error =>{
                    dispatch(saveBookFailure(error.message));
        });
    }
}

const saveBookRequest = Books => {
    return {
        type: SAVE_BOOK_REQUEST,
        payload: Books
    };
};

const savebookSuccess = error => {
    return {
        type: SAVE_BOOK_SUCCESS,
        payload: error 
    };
};

const saveBookFailure = () => {
    return {
        type: SAVE_BOOK_FAILURE
    };
};
