import React from "react";
import { Card, Form, Button ,Col} from "react-bootstrap";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faList, faPlusSquare, faSave } from '@fortawesome/free-solid-svg-icons'

class Book extends React.Component {

    constructor(props){
        super(props);
        this.state={title:'',author:'',coverPhotoUrl:'',isbnNumber:'',price:'',language:''};
        this.bookChange=this.bookChange.bind(this);
        this.submitBook=this.submitBook.bind(this);
    }
    submitBook(event){
        alert("Title: "+this.state.title+
        ", Author:"+this.state.author+
        ", Cover Photo Url: "+this.state.coverPhotoUrl+
        ", ISBN Number: "+this.state.isbnNumber+
        ", Price: "+this.state.price+
        ", Language: "+this.state.language);
        event.preventDefault();
    } 
    bookChange(event){
        this.setState({
            [event.target.name]:event.target.value
        });

    }

    render() {
        return (
            <Card className=" border border-dark bg-dark text-white">
                <Card.Header>                <FontAwesomeIcon  icon ={faPlusSquare}/> Add Books</Card.Header>
                <Form onSubmit={this.submitBook} id="bookFormId">
                <Card.Body>
                   
                      <Form.Row>
                      <Form.Group as ={Col} controlId="formGirdTitle">
                            <Form.Label>Title</Form.Label>
                            <Form.Control type="text" name="title" required
                                value={this.state.title}
                                onChange={this.bookChange}
                                className="bg-dark text-white"
                                placeholder="Enter Book Title" />

                        </Form.Group>
                        <Form.Group as ={Col}  controlId="formGirdAuthor"> 
                            <Form.Label>Author</Form.Label>
                            <Form.Control type="text" name="author" required
                                value={this.state.author}
                                onChange={this.bookChange}
                                className="bg-dark text-white"
                                placeholder="Enter Book Author" />

                        </Form.Group>
                      </Form.Row>

                      
                      <Form.Row>
                      <Form.Group as ={Col} controlId="formGirdCoverPhotoUrl">
                            <Form.Label>Cover Photo URL </Form.Label>
                            <Form.Control type="text" name="coverPhotoUrl" required
                            value={this.state.coverPhotoUrl}
                            onChange={this.bookChange}
                                className="bg-dark text-white"
                                placeholder="Enter Book Cover Photo URL" />

                        </Form.Group>
                        <Form.Group as ={Col} controlId="formGirdISBNNumber" > 
                            <Form.Label>ISBN Number</Form.Label>
                            <Form.Control type="text" name="isbnNumber" required
                            value={this.state.isbnNumber}
                            onChange={this.bookChange}
                                className="bg-dark text-white"
                                placeholder="Enter Book ISBN Number" />

                        </Form.Group>
                      </Form.Row>

                      
                      <Form.Row>
                      <Form.Group as ={Col} controlId="formGirdPrice">
                            <Form.Label>Price</Form.Label>
                            <Form.Control type="text" name="price" required
                               value={this.state.price}
                               onChange={this.bookChange}
                                className="bg-dark text-white" 
                                placeholder="Enter Book Price" />

                        </Form.Group>
                        <Form.Group as ={Col} controlId="formGirdLanguage"> 
                            <Form.Label>Language</Form.Label>
                            <Form.Control type="text" name="language" required
                            value={this.state.language}
                            onChange={this.bookChange}
                                className="bg-dark text-white"
                                placeholder="Enter Book Language" />

                        </Form.Group>
                      </Form.Row>



                </Card.Body>
                <Card.Footer style={{"textAlign":"right"}}>
                <Button variant="success" type="submit">
                <FontAwesomeIcon  icon = {faSave } />
                               Submit
                         </Button>
                </Card.Footer>
                </Form>

            </Card>
        )
    }
}

export default Book;
