import React from "react";
import { Card, Form, Button, Col } from "react-bootstrap";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faPlusSquare, faSave, faUndo } from '@fortawesome/free-solid-svg-icons'

class Book extends React.Component {

    constructor(props) {
        super(props);
       
            this.state = this.initialState;
            this.bookChange = this.bookChange.bind(this);
            this.submitBook = this.submitBook.bind(this);
        }
        initialState = {
            title: '', author: '', coverPhotoUrl: '', isbnNumber: '', price: '', language: ''
        }

        resetBook=()=>{
                this.setState(()=>this.initialState)
        }
        submitBook(event){
            alert("Title: " + this.state.title +
                ", Author:" + this.state.author +
                ", Cover Photo Url: " + this.state.coverPhotoUrl +
                ", ISBN Number: " + this.state.isbnNumber +
                ", Price: " + this.state.price +
                ", Language: " + this.state.language);
            event.preventDefault();
        }
        bookChange(event){
            this.setState({
                [event.target.name]: event.target.value
            });

        }

        render() {
            const { title, author, coverPhotoUrl, isbnNumber, price, language } = this.state;
            return (
                <Card className=" border border-dark bg-dark text-white">
                    <Card.Header>                <FontAwesomeIcon icon={faPlusSquare} /> Add Books</Card.Header>
                    <Form  onReset={this.resetBook} onSubmit={this.submitBook} id="bookFormId">
                        <Card.Body>

                            <Form.Row>
                                <Form.Group as={Col} controlId="formGirdTitle">
                                    <Form.Label>Title</Form.Label>
                                    <Form.Control type="text" name="title" required autoComplete="off"
                                        value={title}
                                        onChange={this.bookChange}
                                        className="bg-dark text-white"
                                        placeholder="Enter Book Title" />

                                </Form.Group>
                                <Form.Group as={Col} controlId="formGirdAuthor">
                                    <Form.Label>Author</Form.Label>
                                    <Form.Control type="text" name="author" required autoComplete="off"
                                        value={author}
                                        onChange={this.bookChange}
                                        className="bg-dark text-white"
                                        placeholder="Enter Book Author" />

                                </Form.Group>
                            </Form.Row>


                            <Form.Row>
                                <Form.Group as={Col} controlId="formGirdCoverPhotoUrl">
                                    <Form.Label>Cover Photo URL </Form.Label>
                                    <Form.Control type="text" name="coverPhotoUrl" required autoComplete="off"
                                        value={coverPhotoUrl}
                                        onChange={this.bookChange}
                                        className="bg-dark text-white"
                                        placeholder="Enter Book Cover Photo URL" />

                                </Form.Group>
                                <Form.Group as={Col} controlId="formGirdISBNNumber" >
                                    <Form.Label>ISBN Number</Form.Label>
                                    <Form.Control type="text" name="isbnNumber" required autoComplete="off"
                                        value={isbnNumber}
                                        onChange={this.bookChange}
                                        className="bg-dark text-white"
                                        placeholder="Enter Book ISBN Number" />

                                </Form.Group>
                            </Form.Row>


                            <Form.Row>
                                <Form.Group as={Col} controlId="formGirdPrice">
                                    <Form.Label>Price</Form.Label>
                                    <Form.Control type="text" name="price" required autoComplete="off"
                                        value={price}
                                        onChange={this.bookChange}
                                        className="bg-dark text-white"
                                        placeholder="Enter Book Price" />

                                </Form.Group>
                                <Form.Group as={Col} controlId="formGirdLanguage">
                                    <Form.Label>Language</Form.Label>
                                    <Form.Control type="text" name="language" required autoComplete="off"
                                        value={language}
                                        onChange={this.bookChange}
                                        className="bg-dark text-white"
                                        placeholder="Enter Book Language" />

                                </Form.Group>
                            </Form.Row>



                        </Card.Body>
                        <Card.Footer style={{ "textAlign": "right" }}>
                            <Button size="sm" variant="success" type="submit" >
                                <FontAwesomeIcon icon={faSave} />  Submit
                         </Button>{' '}
                            <Button size="sm" variant="info" type="reset" >
                                <FontAwesomeIcon icon={ faUndo} />   Reset
                         </Button>
                        </Card.Footer>
                    </Form>

                </Card>
            )
        }
    }

    export default Book;
