import React from "react";
import { Card, Form, Button, Col } from "react-bootstrap";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faEdit, faList, faPlusSquare, faSave, faUndo } from '@fortawesome/free-solid-svg-icons';
import axios from 'axios';
import { connect } from 'react-redux';
import { saveBook,fetchBook,updateBook } from '../services/books/bookActions';

class Book extends React.Component {

    constructor(props) {
        super(props);

        this.state = this.initialState;
        this.bookChange = this.bookChange.bind(this);
        this.submitBook = this.submitBook.bind(this);
    }
    initialState = {
        id: '', title: '', author: '', coverPhotoUrl: '', isbnNumber: '', price: '', language: ''
    }

    componentDidMount() {
        const bookId = +this.props.match.params.id;
        if (bookId) {
            this.findBookbyId(bookId);
        }
    };

    findBookbyId = (bookId) => {
        let book=this.props.bookObject.book;

        if (bookId != null) {
            this.setState({
                id: bookId.id,
                title: bookId.title,
                author: bookId.author,
                coverPhotoUrl: bookId.coverPhotoURL,
                isbnNumber: bookId.isbnNumber,
                price: bookId.price,
                language: bookId.language

            });
        this.props.fetchBook(bookId);


       
     
        
        }

    
